import React from 'react';
import { Link } from 'react-router-dom';

const books = [
  { id: 1, title: "1984", author: "Джордж Оруэлл", year: 1949, rating: "9.2", cover: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1348990566i/5470.jpg" },
  { id: 2, title: "Мастер и Маргарита", author: "Михаил Булгаков", year: 1937, rating: "9.1", cover: "data:image/jpeg;base64," },
  { id: 3, title: "Преступление и наказание", author: "Фёдор Достоевский", year: 1866, rating: "9.0", cover: "https://images-na.ssl-images-amazon.com/images/S/compressed.photo.goodreads.com/books/1382846449i/7144.jpg" },
  { id: 4, title: "Маленький принц", author: "Антуан де Сент-Экзюпери", year: 1943, rating: "8.9", cover: "data:image/jpeg;base64," },
  { id: 5, title: "Гарри Поттер и философский камень", author: "Дж. К. Роулинг", year: 1997, rating: "8.8", cover: "data:image/jpeg;base64," },
  { id: 6, title: "Война и мир", author: "Лев Толстой", year: 1869, rating: "9.3", cover: "data:image/jpeg;base64," },
];

export default function BookList({ addToBasket }) {
  return (
    <div className="container">
      <h1>Классика литературы</h1>
      <div className="books-grid">
        {books.map(book => (
          <div key={book.id} className="book-card">
            <Link to={`/book/${book.id}`}>
              <img src={book.cover} alt={book.title} />
              <div className="book-info">
                <h3>{book.title}</h3>
                <p>{book.author}</p>
                <p className="rating">★ {book.rating}</p>
              </div>
            </Link>
            <button 
              onClick={() => addToBasket(book)}
              style={{
                width: '100%',
                padding: '12px',
                background: 'linear-gradient(45deg, #6b48ff, #4ecdc4)',
                color: 'white',
                border: 'none',
                borderRadius: '0 0 20px 20px',
                fontSize: '1.1rem',
                cursor: 'pointer'
              }}
            >
              Добавить в корзину
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
